package br.com.codecompany.rysys.fgl.jmx;import static br.com.codecompany.rysys.util.Constants.PARAMETER_SEPARATOR;import static br.com.codecompany.rysys.util.Constants.UNNAMED;import br.com.codecompany.rysys.core.jmx.AbstractProbingAspect;import java.io.Serializable;import org.aspectj.lang.annotation.Aspect;import org.aspectj.lang.annotation.Pointcut;@Aspectpublic class FglProbingAspect extends AbstractProbingAspect {	@Pointcut("execution(java.io.Serializable " +	"br.com.codecompany.rysys.fgl.driver.FglAdapter.execute(java.io.Serializable))")	protected void adapterMethodExecution(){};	@Override	protected String extractFunctionName(Serializable command) {		String functionName = UNNAMED;		String[] tokens = String.valueOf(command).split(PARAMETER_SEPARATOR);		if (tokens != null && tokens.length > 2) {			functionName = tokens[2];		}		return functionName;	}	protected boolean isValidFunction(Serializable command) {		return !UNNAMED.equals(extractFunctionName(command));	}}